var idx = lunr(function () {
    this.use(lunr.multiLanguage('en', 'ko', 'zh'));
    this.ref('id');
    this.field('title');
    this.field('subtitle');
    this.field('body');
    this.field('tags');

    documents.forEach(function (doc) {
        this.add(doc);
    }, this);
});

// 검색 결과에서 출력할 excerpt
function extractExcerpt(text, keywords, length = 60) {
    const lowerText = text.toLowerCase();
    let minIndex = -1;
  
    keywords.forEach(keyword => {
        const idx = lowerText.indexOf(keyword.toLowerCase());
        if (idx !== -1 && (minIndex === -1 || idx < minIndex)) {
            minIndex = idx;
        }
    });
  
    if (minIndex === -1) return text.slice(0, length) + '…';
  
    const start = Math.max(0, minIndex - length / 2);
    const end = Math.min(text.length, minIndex + length / 2);
    return (start > 0 ? '…' : '') + text.slice(start, end) + (end < text.length ? '…' : '');
}

// 하이라이팅 함수
function highlightSearchTerms(text, terms) {
    terms.forEach(t => {
      if (t) {
            const regex = new RegExp(`(${t})`, 'gi');
            text = text.replace(regex, '<mark>$1</mark>');
      }
    });
    return text;
}

function lunr_search(term) {
    $('#lunrsearchresults').show(400);
    $("body").addClass("modal-open");
    
    document.getElementById('lunrsearchresults').innerHTML = `
    <div id="resultsmodal" class="modal fade show d-block" tabindex="-1" role="dialog" aria-labelledby="resultsmodal">
        <div class="modal-dialog shadow-lg" role="document">
        <div class="modal-content">
            <div class="modal-header" id="modtit">
            <h5 class='modal-title'>Search results for '${term}'</h5>
            <button type="button" class="close" id="btnx" data-dismiss="modal" aria-label="Close">&times;</button>
            </div>
            <div class="modal-body"><ul class="mb-0"></ul></div>
            <div class="modal-footer">
            <button id="btnx" type="button" class="btn btn-sm" data-dismiss="modal">닫기</button>
            </div>
        </div>
        </div>
    </div>`;

    term = term.trim(); // 앞뒤 공백 제거

    if (!term) {
        document.getElementById('modtit').innerHTML = `
            <h2 style="text-align: left; flex: 1; margin: 0;">
                검색어를 한 글자 이상 입력하세요
            </h2>
            <button type="button" class="close" id="btnx" data-dismiss="modal" aria-label="Close">&times;</button>
        `;
        return false;
    }

    if (term) {
        // 기존 검색 결과 지우기
        document.querySelector(".modal-body ul").innerHTML = '';

        // 한글 또는 한자 포함 여부 확인
        // const isCJK = /[ㄱ-ㅎㅏ-ㅣ가-힣\u4E00-\u9FFF]/.test(term);
        // let query = isCJK ? "*" + term + "*" : term; // 검색어 앞뒤에 * 붙이기

        // 여러 검색어를 분리
        let terms = term.trim().toLowerCase().split(/\s+/); // 배열로 저장

        // 각 키워드에 *를 붙여서 개별 검색
        let partialResults = terms.map(word => {
            return idx.search(`*${word}*`).map(result => result.ref);
        });

        // 모든 검색 결과의 교집합 구하기
        let resultRefs = partialResults.reduce((acc, curr) =>
            acc.filter(ref => curr.includes(ref))
        );

        // 교집합 결과를 기반으로 검색 결과 렌더링
        let results = resultRefs.map(ref => {
            return { ref: ref };
        });

        // 검색어 표시 영역
        if (results.length > 0) {
            const header = document.getElementById('modtit');
            header.innerHTML = ""; // 기존 내용을 비우고
            
            // 제목 요소
            const title = document.createElement("h2");
            title.style.textAlign = "left";
            title.style.flex = "1";
            title.style.margin = "0";
            title.innerHTML = `'<span style="color:#00ab6b">${terms.join(" ")}</span>' 검색 결과 총 ${results.length}건`;
            
            // 닫기 버튼
            const closeBtn = document.createElement("button");
            closeBtn.type = "button";
            closeBtn.className = "close";
            closeBtn.id = "btnx";
            closeBtn.setAttribute("data-dismiss", "modal");
            closeBtn.setAttribute("aria-label", "Close");
            closeBtn.innerHTML = "&times;";
            
            // flex 컨테이너로 스타일 적용
            header.style.display = "flex";
            header.style.alignItems = "center";
            header.style.justifyContent = "space-between";
            
            // 삽입
            header.appendChild(title);
            header.appendChild(closeBtn);

            results.forEach(function(result) {
                var item = documents.find(doc => doc.id == result.ref);
                if (!item) return; // 안전하게 건너뜀

                var listItem = document.createElement("li");

                // 제목
                var link = document.createElement("a");
                link.href = item.url;
                link.innerHTML = highlightSearchTerms(item.title, terms);
                link.style.display = "block";
                link.style.fontWeight = "bold";
                link.style.fontSize = "1rem";
                listItem.appendChild(link);

                // URL 표시
                var urlText = document.createElement("span");
                urlText.textContent = item.url;
                urlText.style.fontSize = "0.8rem";
                urlText.style.color = "rgba(0, 0, 0, .66);";
                urlText.style.display = "block";
                urlText.style.marginBottom = "0.25rem";
                listItem.appendChild(urlText);

                // 하이라이트된 텍스트 일부 보여주기
                var excerpt = document.createElement("p");
                excerpt.className = "search-excerpt";
                excerpt.style.fontSize = "0.9rem";
                excerpt.style.color = "rgba(0, 0, 0, .8);";
                excerpt.innerHTML = highlightSearchTerms(extractExcerpt(item.body, terms), terms);
                listItem.appendChild(excerpt);
                
                document.querySelector(".modal-body ul").appendChild(listItem);
            });
        } else {
        // 검색 결과 없음
            document.getElementById('modtit').innerHTML = `
                <h2 style="text-align: left; flex: 1; margin: 0;">
                    '<span style="color:#00ab6b">${terms.join(" ")}</span>' 검색 결과 없음 😢
                </h2>
                <button type="button" class="close" id="btnx" data-dismiss="modal" aria-label="Close">&times;</button>
            `;
        }
    }
    return false;
}

$(function () {
    $("#lunrsearchresults").on('click', '#btnx', function () {
    $('#lunrsearchresults').hide(5);
    $("body").removeClass("modal-open");
    });
});

// ESC 키로 닫기 기능 추가
document.addEventListener("keydown", function(event) {
    if (event.key === "Escape") {
        const modal = document.getElementById('lunrsearchresults');
        if (modal && modal.style.display !== "none") {
            modal.style.display = "none";
            document.body.classList.remove("modal-open");
        }
    }
});
